function M = mf(x,xS,)    n = size(U,1);    for i = 1:n        if x < xS(1)            M = U(1);            break;        elseif x < xS(i)            h1 = (x-xS(i-1))/(xS(i)-xS(i-1));            h2 = 1-h1;            %%% 分點公式 %%%            M = h1*U(i) + h2*U(i-1);            break;        elseif x > xS(n)             M = U(n);            break;         end    endend